import logo from './logo.svg';
import './App.css';
import React from 'react'

class App extends React.Component { 
      nums = [];
     operation
  constructor(props){ 
    super(props) 

    // Set initial state 
    this.state = {sum : 
          0}


    // Binding this keyword 
    this.updateState = this.updateState.bind(this) 
    }
    updateState(p){ 
      // Changing state 
      this.setState({sum : 
                   p}) 
    } 


  addnum(props) {
    this.nums.push(props)
    }
   op(props) {
    this.operation = props
  }
   sum() {
    if(this.operation == "+"){
      this.state.sum = this.nums[nums.length-1]+this.nums[nums.length]
    }else if(this.operation == "-"){
      this.sum = this.nums[nums.length-1]-this.nums[nums.length]
    }
    else if(operation == "*"){
      sum = nums[nums.length-1]*nums[nums.length]
    }
    else{
      sum = nums[nums.length-1]/nums[nums.length]
    }
  }
  render(){
    <div>
      <div class="result">

        <span>{this.state.sum}</span>
      </div>
     <div class="flex-container">
  <div onClick={this.addnum(1)}>1</div>
  <div onClick={this.addnum(2)}>2</div>
  <div onClick={this.addnum(3)}>3</div>  
  <div onClick={this.op("+")}>+</div>  
</div>
<div class="flex-container">
  <div onClick={this.addnum(4)}>4</div>
  <div onClick={this.addnum(5)}>5</div>
  <div onClick={this.addnum(6)}>6</div>  
  <div onClick={this.op("-")}>-</div>  
</div>
<div class="flex-container">
  <div onClick={this.addnum(7)}>7</div>
  <div onClick={this.addnum(8)}>8</div>
  <div onClick={this.op(9)}>9</div>  
  <div onClick={this.op("*")}>*</div>  
</div>
<div class="flex-container">
  <div onClick={this.op(0)}>0</div>
  <div onClick={this.op(0)}>C.E.</div>
  <div onClick={this.sum()}>=</div>  
  <div onClick={this.op("/")}>/</div>  
</div>
</div>
   }
}

export default App;
