import logo from './logo.svg';
import './App.css';

function App() {
  var nums = [];
  var operation
  var sum
  function addnum(props) {
    nums.push(props)
    }
  function op(props) {
    operation = props
  }
  function sum() {
    if(operation == "+"){
      sum = nums[nums.length-1]+nums[nums.length]
    }
  }
  return (
    <div>
      <div class="result">

        <span>0.0</span>
      </div>
     <div class="flex-container">
  <div onClick={()=>addnum(1)}>1</div>
  <div onClick={()=>addnum(2)}>2</div>
  <div onClick={()=>addnum(3)}>3</div>  
  <div onClick={()=>op("+")}>+</div>  
</div>
<div class="flex-container">
  <div onClick={()=>addnum(4)}>4</div>
  <div onClick={()=>addnum(5)}>5</div>
  <div onClick={()=>addnum(6)}>6</div>  
  <div onClick={()=>op("-")}>-</div>  
</div>
<div class="flex-container">
  <div onClick={()=>addnum(7)}>7</div>
  <div onClick={()=>addnum(8)}>8</div>
  <div onClick={()=>op(9)}>9</div>  
  <div onClick={()=>op("*")}>*</div>  
</div>
<div class="flex-container">
  <div onClick={()=>op(0)}>0</div>
  <div onClick={()=>op(0)}>C.E.</div>
  <div onClick={()=>sum()}>=</div>  
  <div onClick={()=>op("/")}>/</div>  
</div>
</div>
  );
}

export default App;
